package Array;

public class Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// initializing array
        int[] arr = { 1, 2, 3, 4, 5 };

        // size of array
        int n = arr.length;

        for (int i = 0; i < n; i++)
            System.out.print(arr[i] + " ");

	}

}
